import { LockClosedIcon } from "@heroicons/react/20/solid";
import React from "react";
import { useState, useContext } from "react";
import AuthContext from "../../store/auth-context";
import { useNavigate } from "react-router-dom";
import PrefContext from "../../store/pref-context";

const AuthForm = () => {
  const navigate = useNavigate();
  const [email, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const authCtx = useContext(AuthContext);
  const prefContext = useContext(PrefContext);
  const [isLogin, setIsLogin] = useState(true);

  let error;
  if (password && confirmPassword && password !== confirmPassword) {
    error = "Passwords do not match.";
  } else {
    error = "";
  }
  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const submitHandler = function (event) {
    event.preventDefault();
    if (isLogin) {
      const checkAuth = async function () {
        let url =
          "http://trojans-eat.herokuapp.com/api/v1/user/auth/email/" +
          email +
          "/pwd/" +
          password;
        const res = await fetch(url);
        const data = await res.json();

        if (data.length !== 0) {
          authCtx.login(email);
          if (!prefContext.userIsNew) {
            navigate("/", { replace: true });
          } else if (prefContext.userIsNew) {
            prefContext.changeState(false);
            navigate("/Preferences", { replace: true });
          }
        } else {
          alert("Email and password do not match!");
        }
      };
      checkAuth();
    } else {
      const checkRegister = async function () {
        let url =
          "http://trojans-eat.herokuapp.com/api/v1/user/getUser/email/" + email;
        const res = await fetch(url);
        const data = await res.json();

        if (data.length === 0) {
          //if user is not regisetered
          url =
            "http://trojans-eat.herokuapp.com/api/v1/user/register/email/" +
            email +
            /pwd/ +
            password;
          await fetch(url, { method: "POST" });
          setIsLogin(true);
          prefContext.changeState(true);
          navigate("/Auth", { replace: true });
        } else {
          alert("Email is already registered!");
        }
      };
      checkRegister();
    }
  };
  // TailwindUI Template

  return (
    <>
      <div className="flex min-h-full items-center justify-center py-12 px-4 sm:px-6 lg:px-8 lg:my-20">
        <div className="w-full max-w-md space-y-8">
          <div>
            <img
              className="mx-auto h-12 w-auto"
              src="https://upload.wikimedia.org/wikipedia/commons/thumb/9/94/USC_Trojans_logo.svg/1373px-USC_Trojans_logo.svg.png"
              alt="USC Logo"
            />
            <h2 className="mt-6 text-center text-3xl font-bold tracking-tight text-gray-900">
              {isLogin ? "Sign in to your account" : "Create an account"}
            </h2>
          </div>
          <form className="mt-8 space-y-6" onSubmit={submitHandler}>
            <input type="hidden" name="remember" defaultValue="true" />

            {isLogin ? (
              <div className="-space-y-px rounded-md">
                <div>
                  <label htmlFor="email-address" className="sr-only">
                    Email address
                  </label>
                  <input
                    onChange={(e) => setUserName(e.target.value)}
                    id="email-address"
                    name="email"
                    type="email"
                    autoComplete="email"
                    required
                    className="relative block w-full appearance-none rounded-none rounded-t-md border border-gray-300 px-3 py-2 text-gray-900 placeholder-gray-500 focus:z-10 focus:border-red-800 focus:outline-none focus:ring-red-800 sm:text-sm"
                    placeholder="Email address"
                  />
                </div>
                <label htmlFor="password" className="sr-only">
                  Password
                </label>
                <input
                  onChange={(e) => setPassword(e.target.value)}
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="current-password"
                  required
                  className="relative block w-full appearance-none rounded-none rounded-b-md border border-gray-300 px-3 py-2 text-gray-900 placeholder-gray-500 focus:z-10 focus:border-red-800 focus:outline-none focus:ring-red-800 sm:text-sm"
                  placeholder="Password"
                />
              </div>
            ) : (
              <div className="-space-y-px rounded-md ">
                <div>
                  <label htmlFor="email-address" className="sr-only">
                    Email address
                  </label>
                  <input
                    onChange={(e) => setUserName(e.target.value)}
                    id="email-address"
                    name="email"
                    type="email"
                    autoComplete="email"
                    required
                    pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,4}$"
                    className="relative block w-full appearance-none rounded-none rounded-t-md border border-gray-300 px-3 py-2 text-gray-900 placeholder-gray-500 focus:z-10 focus:border-red-800 focus:outline-none focus:ring-red-800 sm:text-sm"
                    placeholder="Email address"
                  />
                </div>
                <div>
                  <label htmlFor="password" className="sr-only">
                    Password
                  </label>
                  <input
                    onChange={(e) => setPassword(e.target.value)}
                    id="Password"
                    name="Password"
                    type="Password"
                    required
                    // ref={passwordInputRef}
                    className="relative block w-full appearance-none rounded-none  border border-gray-300 px-3 py-2 text-gray-900 placeholder-gray-500 focus:z-10 focus:border-red-800 focus:outline-none focus:ring-red-800 sm:text-sm"
                    placeholder="Password"
                  />
                </div>
                <div>
                  <label htmlFor="password" className="sr-only">
                    confirm Password
                  </label>
                  <input
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    id="confirm-password"
                    name="confirm-password"
                    type="password"
                    required
                    className="relative block w-full appearance-none rounded-none rounded-b-md border border-gray-300 px-3 py-2 text-gray-900 placeholder-gray-500 focus:z-10 focus:border-red-800 focus:outline-none focus:ring-red-800 sm:text-sm"
                    placeholder="Confirm Password"
                  />
                </div>
                {error && (
                  <span className="err sm:text-sm text-red-800">{error}</span>
                )}
              </div>
            )}

            <div className="flex items-center justify-between">
              <div className="flex items-center"></div>
              <div className="text-sm">
                <button
                  className="font-medium text-red-800 hover:text-yellow-500 transition"
                  type="button"
                  onClick={switchAuthModeHandler}
                >
                  {isLogin ? "Don't have an account?" : "Already registered?"}
                </button>
              </div>
            </div>
            <div>
              <button
                type="submit"
                className="group relative flex w-full justify-center rounded-md border border-transparent bg-red-800 py-2 px-4 text-sm font-medium text-white hover:bg-red-800 focus:outline-none focus:ring-2 focus:ring-red-800 focus:ring-offset-2"
              >
                <span className="absolute inset-y-0 left-0 flex items-center pl-3">
                  {isLogin ? (
                    <LockClosedIcon
                      className="h-5 w-5 text-red-800 group-hover:text-red-700"
                      aria-hidden="true"
                    />
                  ) : (
                    ""
                  )}
                </span>
                {isLogin ? "Sign in" : "Sign up"}
              </button>
            </div>
          </form>
        </div>
      </div>
    </>
  );
};

export default AuthForm;
