import { LockClosedIcon } from "@heroicons/react/20/solid";
import React from "react";
import { useState, useContext } from "react";
import AuthContext from "../../store/auth-context";
import { useNavigate } from "react-router-dom";
import PrefContext from "../../store/pref-context";

const AuthForm = () => {
  const navigate = useNavigate();
  const [email, setUserName] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const authCtx = useContext(AuthContext);
  const prefContext = useContext(PrefContext);
  const [isLogin, setIsLogin] = useState(true);

  let error;
  if (password && confirmPassword && password !== confirmPassword) {
    error = "Passwords do not match.";
  } else {
    error = "";
  }
  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const submitHandler = function (event) {
    event.preventDefault();
    // let url =
    //   "http://trojans-eat.herokuapp.com/api/v1/user/getUser/email/test@test.com";
    // const test = async () => {
    //   const res = await fetch(url);
    //   console.log(res);
    //   const data = await res.json();
    //   if (data) {
    //     console.log("user exists");
    //   }
    //   console.log(data);
    // };
    // test();
    if (!isLogin) {
      const checkRegister = async function () {
        console.log(email);
        let url =
          "http://trojans-eat.herokuapp.com/api/v1/user/getUser/email/" + email;
        const res = await fetch(url);
        const data = await res.json();
        console.log(data);
        if (data.length === 0) {
          //if user is not regisetered
          console.log(data);
          url =
            "http://trojans-eat.herokuapp.com/api/v1/user/register/email/" +
            email +
            /pwd/ +
            password;
          const response = await fetch(url, { method: "POST" });
          // console.log(response);
          // const data1 = await response.json();
          // console.log(data1);
        } else {
          alert("Email is already registered!");
        }
      };
      checkRegister();
    } else {
      authCtx.login(email);
      if (isLogin) {
        navigate("/", { replace: true });
        // console.log(localStorage.getItem("token"));
      } else {
        setIsLogin(true);
        prefContext.changeState(true);
        navigate("/Auth", { replace: true });
      }
      if (prefContext.userIsNew && authCtx.isLoggedIn) {
        prefContext.changeState(false);
        navigate("/Preferences", { replace: true });
      }
    }

    // const test2 = async () => {
    //   let url =
    //     "http://trojans-eat.herokuapp.com/api/v1/user/auth/email/test1@test.com/pwd/1111";
    //   const res = await fetch(url);
    //   console.log(res);
    //   const data = await res.json();
    //   // if (data) {
    //   //   console.log("user exists");
    //   // }
    //   console.log(data);
    // };
    // test2();
    // console.log(email, password, confirmPassword);

    // let url;
    // if (isLogin) {
    //   // url =
    //   //   "trojans-eat.herokuapp.com/api/v1/user/auth/email/" +
    //   //   email +
    //   //   "/pwd/" +
    //   //   password;
    //   // url =
    //   //   "https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyC61H9pnMsqoh9QPMtIMEjcV8X2D_-tTW0";
    // } else {
    // url = "http://trojans-eat.herokuapp.com/api/v1/user/getUsers";
    // fetch(url).then((response) => {
    //   console.log(response.json());
    // });
    // url =
    //   "trojans-eat.herokuapp.com/api/v1/user/register/" +
    //   email +
    //   "/pwd/" +
    //   password;
    //url= "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyC61H9pnMsqoh9QPMtIMEjcV8X2D_-tTW0";
    // }
    // fetch(url)
    //   .then((res) => {
    //     if (res.ok) {
    //       //the validation checks need to go here (if email exists OR if email/pass dont match)
    //       // console.log(res.json());
    //       // console.log(res);
    //       // res.json();
    //       return res;
    //     } else {
    //       return res.json().then((data) => {
    //         // let errorMessage = "Authentication failed!";
    //         // if (data && data.error && data.error.message) {
    //         //   errorMessage = data.error.message;
    //         // }
    //         // throw new Error(errorMessage);
    //       });
    //     }
    //   })
    // .then((data) => {
    //   // set the token received from the firebase
    //   authCtx.login(email);

    //   if (isLogin) {
    //     navigate("/", { replace: true });
    //     // console.log(localStorage.getItem("token"));
    //   } else {
    //     setIsLogin(true);
    //     prefContext.changeState(true);
    //     navigate("/Auth", { replace: true });
    //   }
    //   if (prefContext.userIsNew && authCtx.isLoggedIn) {
    //     prefContext.changeState(false);
    //     navigate("/Preferences", { replace: true });
    //   }
    // })
    // .catch((err) => {
    //   alert(err.message);
    //   console.log(err);
    // });
  };
  // TailwindUI Template

  return (
    <>
      <div className="flex min-h-full items-center justify-center py-12 px-4 sm:px-6 lg:px-8 lg:my-20">
        <div className="w-full max-w-md space-y-8">
          <div>
            <img
              className="mx-auto h-12 w-auto"
              src="https://upload.wikimedia.org/wikipedia/commons/thumb/9/94/USC_Trojans_logo.svg/1373px-USC_Trojans_logo.svg.png"
              alt="USC Logo"
            />
            <h2 className="mt-6 text-center text-3xl font-bold tracking-tight text-gray-900">
              {isLogin ? "Sign in to your account" : "Create an account"}
            </h2>
          </div>
          <form className="mt-8 space-y-6" onSubmit={submitHandler}>
            <input type="hidden" name="remember" defaultValue="true" />

            {isLogin ? (
              <div className="-space-y-px rounded-md">
                <div>
                  <label htmlFor="email-address" className="sr-only">
                    Email address
                  </label>
                  <input
                    onChange={(e) => setUserName(e.target.value)}
                    id="email-address"
                    name="email"
                    type="email"
                    autoComplete="email"
                    required
                    className="relative block w-full appearance-none rounded-none rounded-t-md border border-gray-300 px-3 py-2 text-gray-900 placeholder-gray-500 focus:z-10 focus:border-red-800 focus:outline-none focus:ring-red-800 sm:text-sm"
                    placeholder="Email address"
                  />
                </div>
                <label htmlFor="password" className="sr-only">
                  Password
                </label>
                <input
                  onChange={(e) => setPassword(e.target.value)}
                  id="password"
                  name="password"
                  type="password"
                  autoComplete="current-password"
                  required
                  className="relative block w-full appearance-none rounded-none rounded-b-md border border-gray-300 px-3 py-2 text-gray-900 placeholder-gray-500 focus:z-10 focus:border-red-800 focus:outline-none focus:ring-red-800 sm:text-sm"
