import React, { useState } from "react";

const PrefContext = React.createContext({
  userIsNew: false,
  checkIfNew: () => {},
});
export const PrefContextProvider = (props) => {
  const [userIsNew, setUserStatus] = useState();
  const contextValue = {
    userStatus: userIsNew,
  };
  const prefRegHandler = function () {
    setUserStatus(userIsNew);
  };
  return (
    <PrefContext.Provider value={contextValue}>
      {props.children}
    </PrefContext.Provider>
  );
};
export default PrefContext;
